import React, {Component} from 'react';
import Board from './Components/Board'

class Body extends Component{
	constructor(props){
		super(props);
		this.state ={
		array:[] 
	}
}	

	componentDidMount() {
		
		fetch('http://localhost:3001/api')
		.then (res => res.json())
		.then (data => this.setState({array: data.rows}));
	}

	

	render() {
		return(
			<div>
				
				<h1>{this.state.array}</h1>
				<Board onSubmit={
        		function(_writer,_title,_desc){
					var writer = _writer;
					var title = _title;
					var desc = _desc;
				}.bind(this)}></Board>
			</div>
		  );
	}
}

export default Body;
