import React, { useState, useRef, useEffect } from "react";
import { Button, Form, Spinner, Alert } from 'react-bootstrap';
import axios from 'axios';
import { useHistory } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { Redirect } from 'react-router-dom';
import { login } from "../Actions/auth";

function LoginPage(props) {
	const [loading, setLoading] = useState(false);
	const [alert, setAlert] = useState(false);
	const [alertMessage, setAlertMessage] = useState(null);
	const { isLoggedIn } = useSelector(state => state.auth);
	const { message } = useSelector(state => state.message);
	const [validated, setValidated] = useState(false);
	const dispatch = useDispatch();

	let _uploadResult;
	let history = useHistory();

	const handleSubmit = (e) => {
		e.preventDefault();
		console.log(isLoggedIn)
		setLoading(true);

		const form = e.currentTarget;
		if (form.checkValidity() === false) {
			setLoading(false);
			e.preventDefault();
			e.stopPropagation();
			setValidated(true);
		}
		else {
			const userID = e.target.login_userID.value;
			const password = e.target.login_password.value;
			dispatch(login(userID, password))
				// .then(() => {
				// 	console.log('isLogedIn: ', isLoggedIn)
				// 	console.log('message: ', message)
				// })
				.catch(() => {
					setLoading(false);
				});
		}

		// axios.post("/login", login_Data)
		// 	.then(
		// 		response => {
		// 			console.log(response)
		// _uploadResult = response.data;
		// if (_uploadResult === 'login success') {
		// 	alert(`Hello`);
		// 	history.push('/home');
		// }
		// else {
		// 	history.push(`/errorpage/${response.status}/${"Registration"}/${response.data}`);
		// }
		// 	}
		// )
		// .catch(function (error) {
		// 	console.log(error);
		// });
	}

	useEffect(() => {
		if (message == 'Login Success.') {
			props.history.push("/home");
			window.location.reload();
		}
		else if (message == 'Wrong password.') {
			setLoading(false);
			setAlert(true);
			setAlertMessage({
				body: "The password you’ve entered is incorrect.",
				forgot: <a>Forgot Password?</a>
			})
		}
		else if (message == 'User Not found.') {
			setLoading(false);
			setAlert(true);
		}
	})
	// if (isLoggedIn) {
	// 	return <Redirect to="/home" />;
	// }

	return (
		<>
			<h1>Login</h1>
			<Form noValidate validated={validated} onSubmit={handleSubmit}>
				<Form.Group >
					<Form.Label>User ID</Form.Label>
					<Form.Control name="login_userID" type="text" placeholder="Enter User ID" required />
				</Form.Group>

				<Form.Group>
					<Form.Label>Password</Form.Label>
					<Form.Control name="login_password" type="password" placeholder="Password" required />
				</Form.Group>
				{alert &&
					<Alert variant="danger" onClose={() => setAlert(false)} dismissible>
						{alertMessage}
					</Alert>
				}
				{loading ?
					<Button variant="primary" type="submit">
						<Spinner
							as="span"
							animation="border"
							size="sm"
							role="status"
							aria-hidden="true"
						/>
					</Button>
					: <Button variant="primary" type="submit">
						Login
					</Button>
				}

				<p>
					Do you need a new account? <a href="/registration">Register</a> here
				</p>
			</Form>
		</>
	);
}

export default LoginPage;
