import React, {Component} from 'react';
import TOC from "./BoardComponents/TOC"
import ReadContent from "./BoardComponents/ReadContent"
import Subject from "./BoardComponents/Subject"
import Control from "./BoardComponents/Control"
import UpdateContent from "./BoardComponents/UpdateContent"
import CreateContent from './BoardComponents/CreateContent';
import axios from 'axios';



class Board extends Component{
	constructor(props){
		super(props);
		
		this.state ={
		mode:'welcome',
      	selected_content_id:0,
      	welcome:{title:'Welcome', desc:'Hello, React!!'},
      	subject:{title:'Welcome to My project1', sub:"Developing Board..."},
		contents:[{id:1, title:'HTML',description:'Html is for information'}],
		message:null
	}
	this.max_content_id=this.max_content_id
}	

	componentWillMount() {
		
		fetch('http://localhost:4000/board/receive')
		.then (res => res.json())
		.then (data => this.setState({contents: data.rows}));
		
		// let data = {
		// 	id:this.state.contents.id,
		// 	write:this.state.contents.writer,
		// 	title:this.state.contents.title,
		// 	description:this.state.contents.description
		// }

		// axios.post('http://localhost:4000/board/receive',
		// 	{data})
		// .then(function (response) {
		// 	console.log(response);
		// 	})
		// 	.catch(function (error) {
		// 	console.log(error);
		// 	});
		
	}

	setData() {
		let data = {
			id:1,
			write:'d',
			title:'d',
			description:'d',
			created:null
		}
		console.log(data);

		// axios.post('http://localhost:4000/board/receive',
		// 	{data})
		// .then(function (response) {
		// 	console.log(response);
		// 	})
		// 	.catch(function (error) {
		// 	console.log(error);
		// 	});

		
	
		// fetch('http://localhost:4000/board/giveresult'
		// ,{
		// method: 'POST',
		// headers:{
		// 	'Content-Type': 'application/json',
    	// 	'Accept': 'application/json'
		// },
		// body:JSON.stringify(data)
    	// })
		// .then (res => res.json())
		// .then (data => console.log(data));
  	}
	

	getReadContent(){
		var i =0;
		  while(i<this.state.contents.length){
			var data = this.state.contents[i];
			if(data.id === this.state.selected_content_id){
			  return data;
			  break;
			}
			i=i+1;
		  }
	  }
	  
	getContent(){
		var _title, _writer, _desc,_article = null;
		if(this.state.mode ==='welcome'){
			// console.log(this.state.contents)
			// this. max_content_id = this.state.contents[this.state.contents.length].id
			// console.log(this.max_content_id)
		}

		//read
		else if (this.state.mode ==='read'){
		  var _content = this.getReadContent();
	
		  _article = <ReadContent writer={_content.writer} title= {_content.title} desc= {_content.description} ></ReadContent>
		}

		// Update
		else if(this.state.mode ==='update'){
		  _content = this.getReadContent();
		  _article = <UpdateContent data = {_content} onSubmit={
			function(_id, _writer, _title, _desc){
			  //add content to this.state.cotnents
			  var _contents = Array.from(this.state.contents);
			  var i =0;
			  while(i<_contents.length){
				if(_contents[i].id ===_id){
				  _contents[i] = {id:_id, writer:_writer, title:_title, description:_desc};
				  break;
				}
				i=i+1;
			  }
			  this.setState({
				contents:_contents,
				mode:'read'
			  });
			this.setData();
			}.bind(this)
		  }></UpdateContent>
		 
		}
		return _article;
	  }

	  creatArticle(){
		var _create = null;
			// Create
			_create = <CreateContent onSubmit={
				function(_writer,_title,_desc){
				//add content to this.state.cotnents
				this.max_content_id= this.state.contents[this.state.contents.length-1].id+1;
				var _contents = Array.from(this.state.contents);
				_contents.push({id:this.max_content_id, writer: _writer, title:_title, description:_desc});
				this.setState({
					contents:_contents,
					mode:'read',
					selected_content_id:this.max_content_id
				});
				this.setData()
				}.bind(this)
			}></CreateContent>
			if(this.state.mode!='update'){
			return _create;
			}
			else return null;	
	  }

	  createController() {
		var _button = <Control onChangeMode={function(_mode){
			if(_mode === 'delete'){
			  if(window.confirm('really?')){
				var _content = Array.from(this.state.contents);
				var i = 0;
				while(i<this.state.contents.length){
				  if(_content[i].id === this.state.selected_content_id){
					_content.splice(i,1);
					break;
				  }
				  i =i+1
				}
				this.setState({
				  mode:'welcome',
				  contents:_content,
				});
				alert('deleted!');
				// this.setData();
			  }
			}
			else{
			this.setState({
			  mode:_mode
			});
			
		  }
		  }.bind(this)}></Control>
		if(this.state.mode != 'welcome'){
		  return _button;
		}
		else return null;
	  }

	  

	  render(){
    
		return(
		<div className="App">
		<Subject title={this.state.subject.title} 
		sub={this.state.subject.sub}
		onChangePage={function(){
		  this.setState({mode:'welcome'});
		  
		}.bind(this)}
		></Subject>

		<TOC onChangePage = {
		  function(id){ 
			this.setState({
			  mode:'read',
			  selected_content_id:Number(id)//문자를 숫자로 변경해주는 함수 Number
			});
		  }.bind(this)
		} data={this.state.contents}></TOC>

		{this.getContent()}
		
		{this.createController()}
	
		{this.creatArticle()}
		</div>
		);
	  }
	}


export default Board;
