import React, { useState, useRef, useEffect } from "react";
import { Rab, Row, Col, Nav, Tab, Form, Button } from 'react-bootstrap';
import { useDispatch, useSelector } from "react-redux";
import { useHistory, Link } from 'react-router-dom';
import ForgotService from '../Services/Forgot.service';
import {
	FIND_ID,
	FIND_PASSWORD,
	NOT_FOUND_ID,
	NOT_FOUND_EMAIL,
	NOT_FOUND_ID_EMAIL,
} from "./type";
import {
	e_email_notfound,
	e_userID_notfound,
	e_userID_email_notfound
} from './message';
import '../css/ForgotIDPassword.css'

function ForgotIDPassword(props) {
	const { isLoggedIn, user } = useSelector(state => state.auth);
	const [type, setType] = useState(props.match.params.type);
	const [alertMessage, setAlertMessage] = useState(null);
	const [alert, setAlert] = useState(false);
	const [loading, setLoading] = useState(false);
	let history = useHistory();
	const findID = (e) => {
		e.preventDefault();
		setLoading(true);
		const forgot_info = {
			userID: null,
			email: e.target.forgotIDPassword_input_email.value,
			type: FIND_ID
		}
		ForgotService.find_ID_password(forgot_info)
			.then((res) => {
				if (res.data.msessage === NOT_FOUND_EMAIL) {
					setLoading(false);
					setAlertMessage({
						body: e_email_notfound,
						forgot: <Link to='/forgot/password'>Sign up for an account.</Link>
					})
				}
				else if (res.data.msessage === NOT_FOUND_ID) {
					setLoading(false);
					setAlertMessage({
						body: e_userID_notfound,
						forgot: <Link to='/forgot/password'>Sign up for an account.</Link>
					})
				}
				else if (res.data.msessage === NOT_FOUND_ID_EMAIL) {
					setLoading(false);
					setAlertMessage({
						body: e_userID_email_notfound,
						forgot: <Link to='/forgot/password'>Sign up for an account.</Link>
					})
				}
			})
	}
	useEffect(() => {
		if (isLoggedIn) {
			return history.push('/home');
		}
	}, [])
	return (
		<div className="forgotIDPassword_whole">

			<Tab.Container id="left-tabs-example" defaultActiveKey={type}>
				<Row>
					<Col sm={3}>
						<Nav variant="pills" className="flex-column">
							<Nav.Item>
								<Nav.Link eventKey="id">Find ID</Nav.Link>
							</Nav.Item>
							<Nav.Item>
								<Nav.Link eventKey="password">Reset Password</Nav.Link>
							</Nav.Item>
						</Nav>
					</Col>
					<Col sm={9}>
						<Tab.Content>
							<Tab.Pane eventKey="id">
								<Form onSubmit={findID}>
									<h1> Find Your ID</h1>
									<Form.Group controlId="formBasicEmail" as={Col} md={{ span: 6, offset: 3 }}>
										<Form.Label>Email address</Form.Label>
										<Form.Control name='forgotIDPassword_input_email' type="email" placeholder="Enter email" />
										<Form.Text className="text-muted">
											Type Email address you entered when you signed up.
    									</Form.Text>
										{alert &&
											<Alert variant="danger" onClose={() => setAlert(false)} dismissible>
												<p>
													{alertMessage.body}
												</p>
												<p>
													{alertMessage.forgot}
												</p>
											</Alert>
										}
										{loading ?
											<Button variant="primary" type="submit">
												<Spinner
													as="span"
													animation="border"
													size="sm"
													role="status"
													aria-hidden="true"
												/>
											</Button>
											: <Button variant="primary" type="submit">
												Submit
											</Button>
										}

									</Form.Group>
								</Form>
							</Tab.Pane>
							<Tab.Pane eventKey="password">
								Reset Password
							</Tab.Pane>
						</Tab.Content>
					</Col>
				</Row>
			</Tab.Container>
		</div>
	)
}

export default ForgotIDPassword;
