import React, {useState, useEffect} from 'react';
import {Button, Image, Card, CardDeck, ListGroup, CardColumns} from 'react-bootstrap';
import '../css/Blog.css'
import axios from 'axios';

function BlogList(props){
	let [data, setData] =  useState(null);
	let donwloadedData='';
	let imagePaths;
	let writer;
	let title;
	let desc;

	useEffect(()=>{
		const res =  axios.post("/blog/list")
		.then (async response => {
			donwloadedData = response.data;
			console.log(donwloadedData);
			
			// for(let i = 0; i<donwloadedData.length; i++){
			
			writer = donwloadedData[0].writer;
			title = donwloadedData[0].title;
			desc = donwloadedData[0].desc;
			imagePaths = donwloadedData[0].imagePaths;

			if(imagePaths != null){
				  let splittedImagePaths = imagePaths.split(',');
				}
			
			// }
		})
	}, []);

	return(
		<div className="blog">
			
			<CardDeck>
				<Card>
					<Card.Img variant="top" src='' />
					<Card.Body>
					<Card.Title>Card title</Card.Title>
					<Card.Text>
						This is a wider card with supporting text below as a natural lead-in to
						additional content. This content is a little bit longer.
					</Card.Text>
					</Card.Body>
					<Card.Footer>
					<small className="text-muted">Last updated 3 mins ago</small>
					</Card.Footer>
				</Card>
				<Card>
					<Card.Img variant="top" src="holder.js/100px160" />
					<Card.Body>
					<Card.Title>Card title</Card.Title>
					<Card.Text>
						This card has supporting text below as a natural lead-in to additional
						content.{' '}
					</Card.Text>
					</Card.Body>
					<Card.Footer>
					<small className="text-muted">Last updated 3 mins ago</small>
					</Card.Footer>
				</Card>
				<Card>
					<Card.Img variant="top" src="holder.js/100px160" />
					<Card.Body>
					<Card.Title>Card title</Card.Title>
					<Card.Text>
						This is a wider card with supporting text below as a natural lead-in to
						additional content. This card has even longer content than the first to
						show that equal height action.
					</Card.Text>
					</Card.Body>
					<Card.Footer>
					<small className="text-muted">Last updated 3 mins ago</small>
					</Card.Footer>
				</Card>
			</CardDeck>
			<Button variant="primary" href='/bloglist/create'>Add Blog</Button>
		</div>

	)

}

export default BlogList;
