import React, {Component, useState} from 'react';
import { Button, FormGroup, Label, Input, Alert } from 'reactstrap';
import bsCustomFileInput from 'bs-custom-file-input'
import axios from 'axios';
import ImageUploader from "react-images-upload";

function CreateBlogList(props){
	const [img, setImage] = useState(null);
	const [result, setResult] = useState(null);
	const [pictures, setPictures] = useState([]);
	const formData = new FormData();

	// let onChange = (e) => {
	// 	setImage(e.target.files[0]);
	//  } 

	 const onDrop = picture => {
		setPictures([...pictures, picture]);
	  };
	  console.log(pictures);

	 const onClick = async () => {
		 if(pictures.length>0){
		 for(let i=0; i<pictures[pictures.length-1].length; i++){
		formData.append('image', pictures[pictures.length-1][i]);
		}
		const res = await axios.post("/blog/create/upload", formData);
	  }
	}
  
	return(
		<div className="container_blog">
			<article>
			  <h2>My Blog</h2>
			  <form action='/blog/create/upload' method="post" enctype="multipart/form-data"
			  onSubmit={function(e){
				  e.preventDefault();
				  console.log(e.target)
				//   this.props.onSubmit(
				// 	//   e.target.writer.value,
				// 	//   e.target.title.value,
				// 	//   e.target.desc.value 
					
				//   );
			  }}>
					<FormGroup>
						<Label for="title">Name</Label>
						<Input type="text" name="title" placeholder="TITLE" />
					</FormGroup>
					<FormGroup>
						<Label for="writer">Title</Label>
						<Input type="text" name="writer" placeholder="NAME" />
					</FormGroup>
					<FormGroup>
						<Label for="desc">Text Area</Label>
						<Input type="textarea" name="desc" placeholder="TEXT" />
					</FormGroup>		
					<ImageUploader
					{...props}
					withIcon={true}
					onChange={onDrop}
					imgExtension={[".jpg", ".gif", ".png", ".gif"]}
					maxFileSize={5242880}
					withPreview={"true"}
					/>
					<Button color="danger" type="submit">Submit</Button>
			  </form>
		  </article>
	
		</div>
		);

}

export default CreateBlogList;
