import React, {Component} from 'react';
import TOC from "./BoardComponents/TOC"
import ReadContent from "./BoardComponents/ReadContent"
import Subject from "./BoardComponents/Subject"
import Control from "./BoardComponents/Control"
import UpdateContent from "./BoardComponents/UpdateContent"
import { render } from '@testing-library/react';
import CreateContent from './BoardComponents/CreateContent';
class Board extends Component{
	constructor(props){
		super(props);
		this.state ={
		mode:'welcome',
      	selected_content_id:2,
      	welcome:{title:'Welcome', desc:'Hello, React!!'},
      	subject:{title:'WEB', sub:"World wide Web"},
		contents:[] 
	}
}	

	componentDidMount() {
		
		fetch('http://localhost:3001/api')
		.then (res => res.json())
		.then (data => this.setState({contents: data.rows}));
	}

	getReadContent(){
		var i =0;
		  while(i<this.state.contents.length){
			var data = this.state.contents[i];
			if(data.id === this.state.selected_content_id){
			  return data;
			  break;
			}
			i=i+1;
		  }
	  }
	  
	  getContent(){
		var _title, _desc,_article = null;
		if(this.state.mode ==='welcome'){
		  _title = this.state.welcome.title;
		  _desc = this.state.welcome.desc;
		  _article = <ReadContent title= {_title} desc= {_desc} ></ReadContent>
		}
		//read
		else if (this.state.mode ==='read'){
		  var _content = this.getReadContent();
	
		  _article = <ReadContent title= {_content.title} desc= {_content.desc} ></ReadContent>
		}
		// Create
		else if(this.state.mode ==='create'){
		  _article = <CreateContent onSubmit={
			function(_title,_desc){
			  //add content to this.state.cotnents
			  this.max_content_id = this.max_content_id+1;
			  //방법1)push를 쓰는 방법은 Original data인 state.contents를 바꾼다
			  // this.state.contents.push(
			  // {id:this.max_content_id, title:_title, desc:_desc});
			  //방법2)퍼포먼스 측면에서 concat이 더 효율적
			  // var _contents = this.state.contents.concat (
			  //   {id:this.max_content_id, title:_title, desc:_desc}
			  // );
			  //방법3)새로운 Array를 Array.from(복사하고싶은배열)/Object.assign({추가하고싶은내용},복사하고싶은객체) api 이용해서 만들어줘서 넣는 방법도 있음
			  var _contents = Array.from(this.state.contents);
			  _contents.push({id:this.max_content_id, title:_title, desc:_desc});
			  this.setState({
				contents:_contents,
				mode:'read',
				selected_content_id:this.max_content_id
			  });
			  console.log(_title, _desc)
			}.bind(this)
		  }></CreateContent>
		}
		// Update
		else if(this.state.mode ==='update'){
		  _content = this.getReadContent();
		  _article = <UpdateContent data = {_content} onSubmit={
			function(_id, _title,_desc){
			  console.log(_id);
			  //add content to this.state.cotnents
			  //immutable(불변) 성능이고 성능을 컨트롤 할때 좋음
			  var _contents = Array.from(this.state.contents);
			  var i =0;
			  while(i<_contents.length){
				if(_contents[i].id ===_id){
				  _contents[i] = {id:_id, title:_title, desc:_desc};
				  break;
				}
				i=i+1;
			  }
			  this.setState({
				contents:_contents,
				mode:'read'
			  });
			}.bind(this)
		  }></UpdateContent>
		}
		return _article;
	  }

	render() {
		// {var result = this.state.array.map(
		// 	function(ab){
		// 	<p key={index}>
		// 	{ab.writer}
		// 	</p>
		// 	}
		// )}
		return(
			<div>
				{console.log(this.state.contents)}
				  <h2>Board</h2>
				  <form action="/create_process" method="post" onSubmit={function(e){
					//   e.preventDefault();
					  this.props.onSubmit(
						  e.target.writer.value,
						  e.target.title.value,
						  e.target.desc.value
					  );
					  
				  }.bind(this)}>
					<p>
						<input type="text" name="writer" placeholder="Writer"></input>
					</p>
					<p>
						<input type="text" name="title" placeholder="title"></input>
					</p>
					<p>
						<textarea  name="desc" placeholder="description"></textarea>
					</p>
					<p>
						<input type="submit"></input>
					</p>
				  </form>

				 {/* {result} */}
				  {/* {var i = 0;
				  	while(i<this.state.array.length){
					'
					Writer: '+ this.state.array[i].writer+
					'Title: '+ this.state.array[i].title +
					Description: ${this.state.array[i].description} 
					'
					i=i+1;
		  		};} */}
			  </div>
		  );
	}
}


export default Board;
