import React, { useState } from 'react';
import { Button, Card, Accordion } from 'react-bootstrap';
import { useHistory } from "react-router-dom";
import { ListGroup, Modal } from 'react-bootstrap';
import axios from 'axios';

function BlogComments(props) {

	let [comments, setComments] = useState(props.comments.comments);
	let [id, setId] = useState(props.comments.id);
	let key_blogComments = -1;
	// const comment_id = props.comment_id;
	const [show, setShow] = useState(false);
	const handleClose = () => setShow(false);
	const handleShow = () => setShow(true);
	let [seeMode, setSeeMode] = useState('See More');

	const delete_comment = () => {
		// axios.post("/blog/deletecomments", comment_id)
		// 	.then(response => {
		// 	})

		handleClose();
	}

	const changeSeeMode = () => {
		if (seeMode === 'See More') setSeeMode('See Less');
		else setSeeMode('See More');
	}


	return (
		<>
			<Card.Header>
				{comments[0]}
				<Accordion.Toggle as={Button} variant="link" eventKey="0">
					<small onClick={changeSeeMode}>{seeMode}</small>
				</Accordion.Toggle>
			</Card.Header>
			{/* <Accordion.Collapse eventKey="0">
				<ListGroup variant="flush">

					{
						comments.map((data, i) => {
							key_blogComments = key_blogComments + 1;
							return (


								<ListGroup.Item key={id[i]}>{data}
									{
										<Button variant="light" size="sm" onClick={handleShow}>Delete</Button>
									}
								</ListGroup.Item>

							)
						})
					}
				</ListGroup> */}

			<Modal show={show} onHide={handleClose}>
				<Modal.Header closeButton>
					<Modal.Title>Delete it ?</Modal.Title>
				</Modal.Header>
				<Modal.Body>Comments will be deleted, permanently</Modal.Body>
				<Modal.Footer>
					<Button variant="secondary" onClick={handleClose}>
						Cancel
          			</Button>
					<Button variant="primary" onClick={delete_comment}>
						Delete
          			</Button>
				</Modal.Footer>
			</Modal>
			{/* </Accordion.Collapse> */}
		</>
	);
}



export default BlogComments;
