import React, {Component} from 'react';
import TOC from "./BoardComponents/TOC"
import ReadContent from "./BoardComponents/ReadContent"
import Subject from "./BoardComponents/Subject"
import Control from "./BoardComponents/Control"
import UpdateContent from "./BoardComponents/UpdateContent"
import CreateContent from './BoardComponents/CreateContent';
class Board extends Component{
	constructor(props){
		super(props);
		
		this.state ={
		mode:'welcome',
      	selected_content_id:2,
      	welcome:{title:'Welcome', desc:'Hello, React!!'},
      	subject:{title:'Welcome to My project1', sub:"Developing...."},
		contents:[] 
	}
	this.max_content_id = this.state.contents.length;
}	

	componentDidMount() {
		
		fetch('http://localhost:3001/api')
		.then (res => res.json())
		.then (data => this.setState({contents: data.rows}));
	}


	getReadContent(){
		var i =0;
		  while(i<this.state.contents.length){
			var data = this.state.contents[i];
			if(data.id === this.state.selected_content_id){
			  return data;
			  break;
			}
			i=i+1;
		  }
	  }
	  

	  getContent(){
		var _title, _writer, _desc,_article = null;
		if(this.state.mode ==='welcome'){
		  _title = this.state.welcome.title;
		  _desc = this.state.welcome.desc;
		  _article = <ReadContent title= {_title} desc= {_desc} ></ReadContent>
		}
		//read
		else if (this.state.mode ==='read'){
		  var _content = this.getReadContent();
	
		  _article = <ReadContent title= {_content.title} desc= {_content.description} ></ReadContent>
		}

		// Update
		else if(this.state.mode ==='update'){
		  _content = this.getReadContent();
		  _article = <UpdateContent data = {_content} onSubmit={
			function(_id, _writer, _title, _desc){
			  //add content to this.state.cotnents
			  var _contents = Array.from(this.state.contents);
			  var i =0;
			  while(i<_contents.length){
				if(_contents[i].id ===_id){
				  _contents[i] = {id:_id, writer:_writer, title:_title, desc:_desc};
				  break;
				}
				i=i+1;
			  }
			  this.setState({
				contents:_contents,
				mode:'read'
			  });
			}.bind(this)
		  }></UpdateContent>
		}
		return _article;
	  }

	  creatArticle(){
		var _create = null;
			// Create
			_create = <CreateContent onSubmit={
				function(_writer,_title,_desc){
				//add content to this.state.cotnents
				this.max_content_id = this.max_content_id+1;
				var _contents = Array.from(this.state.contents);
				_contents.push({id:this.max_content_id, writer: _writer, title:_title, description:_desc});
				this.setState({
					contents:_contents,
					mode:'read',
					selected_content_id:this.max_content_id
				});
				console.log(_title, _desc)
				}.bind(this)
			}></CreateContent>
			if(this.state.mode!='update'){
			return _create;
			}
			else return null;	
	  }

	  render(){
    
		return(
		<div className="App">
		<Subject title={this.state.subject.title} 
		sub={this.state.subject.sub}
		onChangePage={function(){
		  this.setState({mode:'welcome'});
		}.bind(this)}
		></Subject>

		<TOC onChangePage = {
		  function(id){ 
			this.setState({
			  mode:'read',
			  selected_content_id:Number(id)//문자를 숫자로 변경해주는 함수 Number
			});
		  }.bind(this)
		}data={this.state.contents}></TOC>
		{this.getContent()}
		<Control onChangeMode={function(_mode){
		  if(_mode === 'delete'){
			if(window.confirm('really?')){
			  var _content = Array.from(this.state.contents);
			  var i = 0;
			  while(i<this.state.contents.length){
				if(_content[i].id === this.state.selected_content_id){
				  _content.splice(i,1);
				  break;
				}
				i =i+1
			  }
			  this.setState({
				mode:'welcome',
				contents:_content,
				selected_content_id:1
			  });
			  alert('deleted!');
			}
		  }
		  else{
		  this.setState({
			mode:_mode
		  });
		  
		}
		}.bind(this)}></Control>
	
		{this.creatArticle()}
		</div>
		);
	  }
	}


export default Board;
