import React, { useState, useRef, useEffect } from "react";
import { Rab, Row, Col, Nav, Tab, Form, Button } from 'react-bootstrap';
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from 'react-router-dom';
import { service } from '../Services/';
import '../css/ForgotIDPassword.css'

function ForgotIDPassword(props) {
	const { isLoggedIn, user } = useSelector(state => state.auth);
	const [type, setType] = useState(props.match.params.type);
	let history = useHistory();
	const findID = (e) => {
		find_ID(e.target.forgotIDPassword_input_email.value)
	}
	useEffect(() => {
		if (isLoggedIn) {
			return history.push('/home');
		}
	}, [])
	return (
		<div className="forgotIDPassword_whole">

			<Tab.Container id="left-tabs-example" defaultActiveKey={type}>
				<Row>
					<Col sm={3}>
						<Nav variant="pills" className="flex-column">
							<Nav.Item>
								<Nav.Link eventKey="id">Find ID</Nav.Link>
							</Nav.Item>
							<Nav.Item>
								<Nav.Link eventKey="password">Reset Password</Nav.Link>
							</Nav.Item>
						</Nav>
					</Col>
					<Col sm={9}>
						<Tab.Content>
							<Tab.Pane eventKey="id">
								<Form>
									<h1> Find Your ID</h1>
									<Form.Group controlId="formBasicEmail" as={Col} md={{ span: 6, offset: 3 }}>
										<Form.Label>Email address</Form.Label>
										<Form.Control className='forgotIDPassword_input_email' type="email" placeholder="Enter email" />
										<Form.Text className="text-muted">
											Type Email address you entered when you signed up.
    									</Form.Text>
										<Button type='button' onClick={findID}>Submit</Button>
									</Form.Group>
								</Form>
							</Tab.Pane>
							<Tab.Pane eventKey="password">
								Reset Password
							</Tab.Pane>
						</Tab.Content>
					</Col>
				</Row>
			</Tab.Container>
		</div>
	)
}

export default ForgotIDPassword;
