import React, {useState, useEffect} from 'react';
import {Button, Image, Card, CardDeck, ListGroup, CardColumns} from 'react-bootstrap';
import '../css/Blog.css'
import axios from 'axios';

function BlogList(props){
	let [data, setData] =  useState(null);
	let donwloadedData='';

	useEffect(()=>{
		const res =  axios.post("/blog/list")
		.then (async response => {
			donwloadedData = response;
			console.log(donwloadedData);

			// var base64Flag = 'http://localhost:3000/bloglist';
            // var imageStr =
				// await arrayBufferToBase64(donwloadedData.data);
				
			// await setData(base64Flag + imageStr);
			
		})
	}, []);

	return(
		<div className="blog">
			
			<CardDeck>
				<Card>
					<Card.Img variant="top" src={`data:image/jpeg;base64,${donwloadedData.data}`} />
					<Card.Body>
					<Card.Title>Card title</Card.Title>
					<Card.Text>
						This is a wider card with supporting text below as a natural lead-in to
						additional content. This content is a little bit longer.
					</Card.Text>
					</Card.Body>
					<Card.Footer>
					<small className="text-muted">Last updated 3 mins ago</small>
					</Card.Footer>
				</Card>
				<Card>
					<Card.Img variant="top" src="holder.js/100px160" />
					<Card.Body>
					<Card.Title>Card title</Card.Title>
					<Card.Text>
						This card has supporting text below as a natural lead-in to additional
						content.{' '}
					</Card.Text>
					</Card.Body>
					<Card.Footer>
					<small className="text-muted">Last updated 3 mins ago</small>
					</Card.Footer>
				</Card>
				<Card>
					<Card.Img variant="top" src="holder.js/100px160" />
					<Card.Body>
					<Card.Title>Card title</Card.Title>
					<Card.Text>
						This is a wider card with supporting text below as a natural lead-in to
						additional content. This card has even longer content than the first to
						show that equal height action.
					</Card.Text>
					</Card.Body>
					<Card.Footer>
					<small className="text-muted">Last updated 3 mins ago</small>
					</Card.Footer>
				</Card>
			</CardDeck>
			<Button variant="primary" href='/bloglist/create'>Add Blog</Button>
		</div>

	)

}

export default BlogList;
