	let retrievedData = {};
	let splittedImagePaths = [];
	let [data, setData] = useState(null);
	useEffect(() => {
		const res = axios.post("/blog/retrieveblog")
			.then(response => {
				retrievedData = response.data;
				console.log(response.data);

				splittedImagePaths = retrievedData.imagePaths.split(',');
