import React, { useState, useEffect } from 'react';
import { Modal, Carousel, Button, Image, Card, CardDeck, ListGroup, CardColumns } from 'react-bootstrap';
import '../css/BlogList.css'
import axios from 'axios';
import ReadBlogList from './BlogComponents/ReadBlogList';

function BlogList(props) {

	let splittedImagePaths

	let [allBlogs, setAllBlogs] = useState([]);
	// let show = null;
	const [show, setShow] = useState(false);
	const [showing, setShowing] = useState(null);
	const handleClose = () => setShow(false);
	const handleShow = (id) => {
		setShow(true);
		// doing(id);
		console.log(id)
	}

	useEffect(() => {
		const res = axios.post("/blog/list")
			.then(response => {
				console.log(response.data);
				setAllBlogs(response.data);

			})
	}, []);


	// function doing(id) {
	// 	let i = 0;
	// 	while (i < allBlogs.length) {
	// 		let blog_id = allBlogs.id;
	// 		console.log(id)
	// 		if (id === blog_id) {
	// 			splittedImagePaths = allBlogs.imagePaths.split(',');

	// 			for (let a = 0; a < splittedImagePaths.length; a++) {
	// 				splittedImagePaths[a] = splittedImagePaths[a].substring(16, splittedImagePaths[a].length);
	// 				console.log(splittedImagePaths[a]);
	// 			}
	// 			return (

	// 				<ReadBlogList key={allBlogs.id}
	// 					writer={allBlogs.writer}
	// 					title={allBlogs.title}
	// 					desc={allBlogs.desc}
	// 					imagePathArray={splittedImagePaths}
	// 					createdAt={allBlogs.createdAt}
	// 					updatedAt={allBlogs.updatedAt}>
	// 				</ReadBlogList>

	// 			);
	// 		}
	// 		i = i + 1;
	// 	}
	// }

	return (
		<div className="blogList_whole">
			<Button className="blogList_addBlog_button" variant="primary" href='/bloglist/create'>Add Blog</Button>
			<br></br>
			<div className="blogList_list">
				{
					allBlogs.map((data) => {

						splittedImagePaths = data.imagePaths.split(',');
						for (let a = 0; a < splittedImagePaths.length; a++) {
							splittedImagePaths[a] = splittedImagePaths[a].substring(16, splittedImagePaths[a].length);
							console.log(splittedImagePaths[a]);
						}

						return (
							<>
								<Button variant="primary" onClick={handleShow(data.id)} key={data.id}>
									Launch demo modal
								</Button>
								{/* {() => {if(data.id == showingKeyNumber) setShow(true);
						else setShow(false)}} */}


							</>
						);
					})

				}
				{/* < Modal show={show} onHide={handleClose}>
					{doing()}
				</Modal> */}
			</div>
		</div>
	);
}


export default BlogList;
