import React, {useState, useEffect} from 'react';
import ReturnCarousel from './ReturnCarousel'
import {Card, CardDeck, Carousel} from 'react-bootstrap';

function ReadBlogList(props) {
	let imagePathArray =[];
	let carousels;
	let title = props.title;
	let desc = props.desc;
	let writer = props.writer;

	function returnCarousels() {
		imagePathArray = props.imagePathArray
		for(let i =0; i<imagePathArray.length; i++){
			carousels += <ReturnCarousel imagePath = {imagePathArray[i]}></ReturnCarousel>
	}
		return carousels;
	}

	return(
		<div>
			<CardDeck>
			<Card>
				<Carousel>
				{returnCarousels()}
				</Carousel>
				<Card.Body>
				<Card.Title>{title}</Card.Title>
				<Card.Text>
						{desc}
				</Card.Text>
				</Card.Body>
				<Card.Footer>
					<small className="text-muted">writer:{writer}</small>
				</Card.Footer>
			</Card>
			</CardDeck>
			</div>
	);
}

export default ReadBlogList;
