import React, {useState, useEffect} from 'react';
import {Carousel, Button, Image, Card, CardDeck, ListGroup, CardColumns} from 'react-bootstrap';
import '../css/Blog.css'
import axios from 'axios';

function BlogList(props){
	let [data, setData] =  useState(null);

	let donwloadedData='';
	let imagePaths;
	let writer;
	let title;
	let desc;
	let splittedImagePaths

	let [showAllBlogs, setShowAllBlogs] = useState(null);

	useEffect(()=>{
		const res =  axios.post("/blog/list")
		.then (response => {
			donwloadedData = response.data;
			console.log(donwloadedData);
			
			for(let i = 0; i<donwloadedData.length; i++){
			
			writer = donwloadedData[i].writer;
			title = donwloadedData[i].title;
			desc = donwloadedData[i].desc;
			imagePaths = donwloadedData[i].imagePaths;

			if(imagePaths != null){
				  splittedImagePaths = imagePaths.split(',');
				for(let a= 0; a < splittedImagePaths.length; a++){
					splittedImagePaths[a] = splittedImagePaths[a].substring(1, splittedImagePaths[a].length);
					splittedImagePaths[a] = '../..'+splittedImagePaths[a];
					console.log(splittedImagePaths[a]);
				}
			}

				setShowAllBlogs (
				<div>
				<CardDeck>
				<Card>
					{/* <Card.Img variant="top" src={require('./1.jpg')} /> */}
					<Carousel>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src="holder.js/800x400?text=First slide&bg=373940"
      alt="First slide"
    />
    <Carousel.Caption>
      <h3>First slide label</h3>
      <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
    </Carousel.Caption>
  </Carousel.Item>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src="holder.js/800x400?text=Second slide&bg=282c34"
      alt="Third slide"
    />

    <Carousel.Caption>
      <h3>Second slide label</h3>
      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
    </Carousel.Caption>
  </Carousel.Item>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src="holder.js/800x400?text=Third slide&bg=20232a"
      alt="Third slide"
    />

    <Carousel.Caption>
      <h3>Third slide label</h3>
      <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
    </Carousel.Caption>
  </Carousel.Item>
</Carousel>
					<Card.Body>
					<Card.Title>{title}</Card.Title>
					<Card.Text>
							{desc}
					</Card.Text>
					</Card.Body>
					<Card.Footer>
			          <small className="text-muted">writer:{writer}</small>
					</Card.Footer>
				</Card>
				</CardDeck>
				</div>
				)
			}
		})
	}, []);

	return(
		<div className="blog">
			{showAllBlogs}
			
			<Button variant="primary" href='/bloglist/create'>Add Blog</Button>
		</div>

	)

}

export default BlogList;
