import React, {useState, useEffect} from 'react';
import {Modal, Carousel, Button, Image, Card, CardDeck, ListGroup, CardColumns} from 'react-bootstrap';
import '../css/BlogList.css'
import axios from 'axios';
import ReadBlogList from './BlogComponents/ReadBlogList';

function BlogList(props){

	let splittedImagePaths

	let [allBlogs, setAllBlogs] = useState([]);
	// let show = null;
	const [show, setShow] = useState(false);
	const [showingKeyNumber, setshowingKeyNumber] = useState(null);
	const handleClose = () => show = false;
	// const handleShow = () => setShow(true);

	useEffect(()=>{
		const res =  axios.post("/blog/list")
		.then (async response => {
			await console.log(response.data);
			await setAllBlogs(response.data);
			
		})
	}, []);

	return(
		<div className="blogList_whole">
			<Button className="blogList_addBlog_button" variant="primary" href='/bloglist/create'>Add Blog</Button>
				<br></br>
			<div className="blogList_list">
			{
				allBlogs.map((data) => {
					
					splittedImagePaths = data.imagePaths.split(',');

					for(let a= 0; a < splittedImagePaths.length; a++){
						splittedImagePaths[a] = splittedImagePaths[a].substring(16, splittedImagePaths[a].length);
						console.log(splittedImagePaths[a]);
					}
					console.log(data);

					
					return(
						<>
						<Button variant="primary" onClick={setshowingKeyNumber(data.id)} key={data.id}>
							Launch demo modal
						</Button>	
						
						

						<Modal show={show} onHide={handleClose}>
							<ReadBlogList key={data.id} 
							writer={data.writer} 
							title={data.title} 
							desc={data.desc} 
							imagePathArray={splittedImagePaths} 
							createdAt ={data.createdAt} 
							updatedAt ={data.updatedAt}>
							</ReadBlogList>
						</Modal>
						</>
					);			
				})	
			}
			</div>
		</div>
	)

}

export default BlogList;
