import React, { useState } from 'react';
import { Button, Card, Accordion, InputGroup, FormControl, ListGroup, Modal } from 'react-bootstrap';
import { useHistory } from "react-router-dom";
import axios from 'axios';

function BlogComments(props) {

	let [comments, setComments] = useState(props.comments.comments);
	let [comment_id, setComment_id] = useState(props.comments.id);
	let [seeMode, setSeeMode] = useState('See More');
	let [seeMode_commentSubmitButton, setCommentButton] = useState(true);
	let id = props.id;
	let key_blogComments = -1;
	let _uploadResult = '';
	let history = useHistory();
	// const comment_id = props.comment_id;
	const [show, setShow] = useState(false);
	const handleClose = () => setShow(false);
	const handleShow = () => setShow(true);

	const sendComment = async (e) => {
		e.preventDefault();
		let comment =
		{
			body: e.target.comment_form_control.value,
			id: id
		}
		await axios.post("/blog/comments", comment)
			.then(
				response => {
					_uploadResult = response.data;
					if (_uploadResult === 'Upload success') {
						setComments(comments => [comment.body, ...comments]);
					}
					else history.push('/errorpage');
				})
			.catch(function (error) {
				console.log(error);
			});
	}

	const commentSubmitButton = (e) => {
		let comment = e.target.value;
		console.log(comment)
		if (comment.length > 0) setCommentButton(false);
		else setCommentButton(true);
	}

	const delete_comment = () => {

		handleClose();
	}

	const changeSeeMode = () => {
		if (seeMode === 'See More') setSeeMode('See Less');
		else setSeeMode('See More');
	}

	// useEffect(() => {
	// 	return () => {
	// 		axios.post("/blog/deletecomments", comment_id)
	// 			.then(response => {
	// 			})
	// 	}
	// }, []);

	return (
		<>
			<form action='' method="post" onSubmit={sendComment}>
				{
					<Accordion >
						<Card>
							<Card.Header>
								{comments[0]}
								{(comments.length === 0) ? <small>No comments</small> :
									<>
										<Button variant="light" size="sm" onClick={handleShow} className="blogComments_deleteButton">Delete</Button>

										<Accordion.Toggle as={Button} variant="link" eventKey="0">
											<small onClick={changeSeeMode}>{seeMode}</small>
										</Accordion.Toggle>
									</>
								}
							</Card.Header>
							<Accordion.Collapse eventKey="0">
								<ListGroup variant="flush">

									{
										// To show the first comment at the top (first comment is substracted)
										comments.slice(1, comments.length).map((data, i) => {
											key_blogComments = key_blogComments + 1;
											return (
												<ListGroup.Item key={comment_id[i]}>{data}
													{
														<Button variant="light" size="sm" onClick={handleShow} className="blogComments_deleteButton">Delete</Button>
													}
												</ListGroup.Item>
											)
										})
									}
								</ListGroup>
							</Accordion.Collapse>
							<Modal show={show} onHide={handleClose}>
								<Modal.Header closeButton>
									<Modal.Title>Delete it ?</Modal.Title>
								</Modal.Header>
								<Modal.Body> Delete this comment, permanently ?</Modal.Body>
								<Modal.Footer>
									<Button variant="secondary" onClick={handleClose}>
										Cancel
          							</Button>
									<Button variant="primary" onClick={delete_comment}>
										Delete
          							</Button>
								</Modal.Footer>
							</Modal>

							<InputGroup className="comment_input_group">
								<FormControl
									placeholder="Comment"
									aria-label="Comment"
									aria-describedby="blog_comment"
									name="comment_form_control"
									onChange={commentSubmitButton}
								/>
								<InputGroup.Append>
									<Button variant="outline-secondary" type="submit" disabled={seeMode_commentSubmitButton}>Submit</Button>
								</InputGroup.Append>
							</InputGroup>
						</Card>
					</Accordion>
				}
			</form>
		</>
	);
}



export default BlogComments;
