import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, FormGroup, Label, Input } from 'reactstrap';
import ImageUploader from "react-images-upload";
import { useHistory } from "react-router-dom";


function UpdateBlogList(props) {

	const [pictures, setPictures] = useState([]);
	const [imgUploader, setImgUploader] = useState(false);
	const [imgButton, setImgButton] = useState(true);
	const onDrop = picture => {
		setPictures([...pictures, picture]);
	};
	let retrievedData;
	let info =
	{
		id: props.match.params.id,
		writer: props.match.params.author
	}
	let history = useHistory();

	let splittedImagePaths = [];
	let [data, setData] = useState(null);
	useEffect(() => {
		const res = axios.post("/blog/retrieveblog", info)
			.then(response => {
				retrievedData = response.data;
				console.log(retrievedData);
				if (retrievedData.id != null) {
					splittedImagePaths = retrievedData.imagespath.split(',');
					for (let a = 0; a < splittedImagePaths.length; a++) {
						splittedImagePaths[a] = splittedImagePaths[a].substring(16, splittedImagePaths[a].length);
					}
				}
				else history.push('/errorpage');
			})
		console.log('data:' + retrievedData)
	})

	const showImgUploader = () => {
		setImgUploader(true);
		setImgButton(false);
	}

	return (
		<div className='updateBlogList_whole'>
			<FormGroup>
				<Label for="title">Title</Label>
				<Input type="text" name="title" placeholder="TITLE" />
			</FormGroup>
			<FormGroup>
				<Label for="writer">Writer</Label>
				<Input type="text" name="writer" placeholder="NAME" />
			</FormGroup>
			<FormGroup>
				<Label for="desc">Text Area</Label>
				<Input type="textarea" name="desc" placeholder="TEXT" />
			</FormGroup>

			{ imgButton ?
				<Button variant="secondary" onClick={showImgUploader} size="lg" block>
					Update Image
  			</Button>
				: null
			}
			{ imgUploader ?
				<ImageUploader
					name="imageUploader"
					{...props}
					withIcon={true}
					onChange={onDrop}
					imgExtension={[".jpg", ".gif", ".png", ".gif"]}
					maxFileSize={5242880}
					withPreview={true}
				/>
				: null
			}
			<Button color="primary" type="submit">Submit</Button>
		</div>

	);
}



export default UpdateBlogList;
