import React, { useState } from "react";
import { Button, Form, Col, InputGroup } from 'react-bootstrap';
// import { Formik } from 'formik';
// let yup = require('yup');
// const { Formik } = formik;


function Register(props) {
	const [validated, setValidated] = useState(false);
	const [userID, setUserId] = useState(null);
	const [password, setPassword] = useState(null);
	const [confirmPassword_invalid, setConfirmPassword_invalid] = useState();
	const [firstName, setFirstName] = useState(null);
	const [lastName, setLastName] = useState(null);
	const [email, setEmail] = useState(null);
	const [userIdFeedBack, setUserIdFeedBack] = useState(null);
	const [userIdInvalid, setUserIdInvalid] = useState();

	const [passwordInvalid, setPasswordInvalid] = useState();
	const [buttonInvalid, setButtonInvalid] = useState(false);



	const handleSubmit = (event) => {
		const form = event.currentTarget;
		if (form.checkValidity() === false) {
			event.preventDefault();
			event.stopPropagation();
			setValidated(true);
		}
		else {
		}

	};

	const onChangeUserID = (e) => {
		const userID = e.target.value;
		if (userID === undefined || userID.length < 9 || userID.length > 20) {
			setUserIdInvalid(true);
			setUserIdFeedBack("Please choose a user ID with 8 to 20 characters.");
		}
		else {
			setUserIdInvalid(false);
			setUserId(userID);
			console.log(userIdInvalid, ' ', confirmPassword_invalid, ' ', passwordInvalid)
			if (!userIdInvalid && !confirmPassword_invalid && !passwordInvalid) {
				setButtonInvalid(true)
			}
			else setButtonInvalid(false)
		}


	};

	const onChangePassword = (e) => {
		const password = e.target.value;
		if (userID === undefined || password.length < 9 || password.length > 20) {
			setPasswordInvalid(true);
		}
		else {
			setPasswordInvalid(false);
			setPassword(password);
			console.log(userIdInvalid, ' ', confirmPassword_invalid, ' ', passwordInvalid)
			if (!userIdInvalid && !confirmPassword_invalid && !passwordInvalid) {
				setButtonInvalid(true)
			}
			else setButtonInvalid(false)
		}

	};

	const onChangeConfirmPassword = (e) => {
		const confirm_password = e.target.value;
		if (confirm_password === password) {
			setConfirmPassword_invalid(() => false);
			setPassword(confirm_password);
		}
		else {
			setConfirmPassword_invalid(true);
			console.log(userIdInvalid, ' ', confirmPassword_invalid, ' ', passwordInvalid)
			if (!userIdInvalid && !confirmPassword_invalid && !passwordInvalid) {
				setButtonInvalid(true)
			}
			else setButtonInvalid(false)
		}
	};

	const onChangeFirstName = (e) => {
		const first_Name = e.target.value;
		setFirstName(firstName);
	};

	const onChangeLastName = (e) => {
		const last_Name = e.target.value;
		setLastName(last_Name);
	};

	const onChangeEmail = (e) => {
		const e_mail = e.target.value;
		setEmail(e_mail);
	}

	return (
		<Form noValidate validated={validated} onSubmit={handleSubmit}>
			<Form.Group as={Col} md="4" controlId="userId">
				<Form.Label>User ID</Form.Label>
				<Form.Control type="text" placeholder="User ID" onChange={onChangeUserID} isInvalid={userIdInvalid} />
				<Form.Control.Feedback type="invalid">
					{userIdFeedBack}
				</Form.Control.Feedback>
			</Form.Group>

			<Form.Group as={Col} md="4" controlId="password">
				<Form.Label>Password</Form.Label>
				<Form.Control type="password" placeholder="Password" onChange={onChangePassword} isInvalid={passwordInvalid} required />
				<Form.Control.Feedback type="invalid">
					Please choose a new password with at least 8 characters
          		</Form.Control.Feedback>
			</Form.Group>

			<Form.Group as={Col} md="4" controlId="confirm_password">
				<Form.Label>Confirm Password</Form.Label>
				<Form.Control
					type="password"
					placeholder="Confirm Password"
					required
					isInvalid={confirmPassword_invalid}
					onChange={onChangeConfirmPassword}
				/>
				<Form.Control.Feedback type="invalid">
					Please make sure this matches your password..
          		</Form.Control.Feedback>
			</Form.Group>

			<Form.Group as={Col} md="4" controlId="firstName">
				<Form.Label>First name</Form.Label>
				<Form.Control
					required
					type="text"
					placeholder="First name"
					onChange={onChangeFirstName}
				/>
				<Form.Control.Feedback type="invalid">
					Please provide a valid First Name.
					</Form.Control.Feedback>
			</Form.Group>

			<Form.Group as={Col} md="4" controlId="lastName">
				<Form.Label>Last name</Form.Label>
				<Form.Control
					required
					type="text"
					placeholder="Last name"
					onChange={onChangeLastName}
				/>
				<Form.Control.Feedback type="invalid">
					Please provide a valid Last Name.
				</Form.Control.Feedback>
			</Form.Group>


			<Form.Group as={Col} md="4" controlId="email">
				<Form.Label>Email address</Form.Label>
				<Form.Control type="email" placeholder="Email address" onChange={onChangeEmail} required />
				<Form.Control.Feedback type="invalid">
					Please provide a valid Email.
         		</Form.Control.Feedback>
			</Form.Group>
			{ buttonInvalid ?
				<Button type="submit">Submit</Button>
				: <Button type="submit" disabled >Submit</Button>

			}
		</Form>
	);
}

export default Register;
