import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from "react-redux";
import { CardDeck, Card, Image, ListGroup, Button, Col, Form, Row } from 'react-bootstrap';
import { Redirect } from 'react-router-dom';
import ImageUploader from 'react-images-upload';
import '../css/MyAccount.css';
import { userUpdate } from "../Actions/auth";
import { useHistory } from "react-router-dom";
import {
	d_userMessage,
	d_emailMessage,
	v_passwordMessage,
	v_confirmPasswordMessage,
	v_idMessage,
	v_emailMessage,
	v_fNMessage,
	v_lNMessage
} from "./message";
import {
	RESET_PASSWORD,
	USER_UPDATE,
	WRONG_PASSWORD,
	USER_NOT_FOUND,
	USER_UPDATE_SUCCESS
} from "./type";

function MyAccount(props) {
	const { isLoggedIn, user } = useSelector(state => state.auth);
	const { message } = useSelector(state => state.message);
	const IMG_WIDTH = 171;
	const IMG_LENGTH = 180;
	const [userEdit, setUserEdit] = useState(false);
	const [validated, setValidated] = useState(false);
	const [firstName, setFirstName] = useState(user.firstName);
	const [lastName, setLastName] = useState(user.lastName);
	const [email, setEmail] = useState(user.email);
	const [emailInvalid, setEmailInvalid] = useState();
	const [emailFeedBack, setEmailFeedBack] = useState(null);
	const [pictures, setPictures] = useState(null);
	const [showResetPassword, setShowResetPassword] = useState(true);
	const [passwordInvalid, setPasswordInvalid] = useState();
	const [password, setPassword] = useState(null);
	const [confirmPassword, setConfirmPassword] = useState(null);
	const [confirmPassword_invalid, setConfirmPassword_invalid] = useState();
	const [resetPasswordValidated, setResetPasswordValidated] = useState(false);

	const dispatch = useDispatch();
	let [profileImageURL, setProfileImageURL] = useState(null);
	let imagePath;
	let history = useHistory();
	let _password;
	let _confirm_password;

	//Setting a Image Path 
	imagePath = user.profileImg;
	if (imagePath != null) {
		imagePath = imagePath.substring(16, imagePath.length);
	}
	else imagePath = "./uploadImages/icon/img_user.jpg"

	console.log(message)
	useEffect(() => {

		if (message == USER_UPDATE_SUCCESS) {
			setShowResetPassword(true);
			setUserEdit(false);
			dispatch({
				type: SET_MESSAGE,
				payload: null,
			});
		}
		else if (message == WRONG_PASSWORD) {
			// setLoading(false);
			// setAlert(true);
			// setAlertMessage({
			// 	body: l_wrongPassword,
			// 	forgot: <Link to='/forgot'>Forgot ID or Password?</Link>
			// })
		}
		else if (message == USER_NOT_FOUND) {
			// setLoading(false);
			// setAlert(true);
			// setAlertMessage({
			// 	body: l_wrongUser,
			// 	forgot: <Link href='/forgot'>Forgot ID or Password?</Link>
			// })
		}
	})

	//USER_EDIT METHOD
	const editUser = () => {
		setUserEdit(true);
	}

	const cancelEdit = () => {
		setUserEdit(false);
		setFirstName(user.firstName);
		setLastName(user.lastName);
		setEmail(user.email);
		setProfileImageURL(null);
	}

	const handleSubmit = (event) => {
		event.preventDefault();
		const form = event.currentTarget;
		if (form.checkValidity() === false) {
			event.stopPropagation();
			setValidated(true);
		}
		else {
			const user_data = {
				userID: user.userID,
				firstName: firstName,
				lastName: lastName,
				email: email,
				type: USER_UPDATE
			}
			console.log(user_data)
			dispatch(userUpdate(user_data, pictures));
			setFirstName(user.firstName);
			setLastName(user.lastName);
			setEmail(user.email);
			setProfileImageURL(null);
		}
		console.log(message)
	}

	const onChangeFirstName = (e) => {
		const first_Name = e.target.value;
		setFirstName(first_Name);
	};

	const onChangeLastName = (e) => {
		const last_Name = e.target.value;
		setLastName(last_Name);
	};

	const onChangeEmail = (e) => {

		const e_mail = e.target.value;
		setEmail(e_mail);
		const regExp = /^[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*@[0-9a-zA-Z]([-_\.]?[0-9a-zA-Z])*\.[a-zA-Z]{2,3}$/i;
		if (!regExp.test(e_mail)) {
			setEmailInvalid(true);
			setEmailFeedBack(v_emailMessage);
		}
		else {
			setEmailInvalid(false);
			setEmail(e_mail);
		}
	}

	const uploadSingleFile = picture => {
		setProfileImageURL(URL.createObjectURL(picture[0]));
		setPictures(picture[0]);
	}

	//RESET_PASSWORD METHOD
	//validating password, password must be between 8 and 20 letter.
	const onChangePassword = (e) => {
		_password = e.target.value;
		if (_password === undefined || _password.length < 8 || _password.length > 20) {
			setPasswordInvalid(true);
			setPassword(_password);
		}
		else if (confirmPassword != null && _password != confirmPassword) {
			setPasswordInvalid(false);
			setConfirmPassword_invalid(true);
			setPassword(_password);
		}
		else if (confirmPassword != null && _password === confirmPassword) {
			setPasswordInvalid(false);
			setConfirmPassword_invalid(false);
			setPassword(_password);
		}
		else {
			setPasswordInvalid(() => false);
			setPassword(_password);
			if (_password != confirmPassword) {
				setConfirmPassword_invalid(true);
			}
		}
	};

	//validating confirm password, confirm password must be between 8 and 20 letter and the same as password.
	const onChangeConfirmPassword = (e) => {

		_confirm_password = e.target.value;
		if (_confirm_password === undefined || _confirm_password.length < 8 || _confirm_password.length > 20) {
			setConfirmPassword_invalid(true);
			setConfirmPassword(_confirm_password);
		}
		else if (password != null && _confirm_password != password) {
			setConfirmPassword_invalid(true);
			setConfirmPassword(_confirm_password);
		}
		else if (password != null && _confirm_password === password) {
			setConfirmPassword_invalid(false);
			setConfirmPassword(_confirm_password);
		}
		else {
			setConfirmPassword_invalid(true);
			setConfirmPassword(_confirm_password);
		}
	};

	const resetPasswordSubmit = (event) => {
		event.preventDefault();
		const form = event.currentTarget;
		if (form.checkValidity() === false) {
			event.stopPropagation();
			setResetPasswordValidated(true);
		}
		else {

			const user_data = {
				userID: user.userID,
				currentPassword: event.target.myAccount_resetPassword_currentPassword.value,
				newPassword: password,
				type: RESET_PASSWORD
			}
			console.log(user_data)
			dispatch(userUpdate(user_data));
		}

	}
	if (!isLoggedIn) {
		return <Redirect to="/home" />;
	}

	return (
		<CardDeck className="myAccount">
			{userEdit ?
				<div className="myAccount_userInformation">
					<Card >
						< Form
							noValidate validated={validated}
							onSubmit={handleSubmit}
						>
							<h2 id="myAccount_greeting">Hello {user.firstName} {user.lastName}</h2>
							{profileImageURL != null ?
								<Image variant="top"
									className="myAccount_profileImage"
									src={profileImageURL}
									width={171}
									height={180}
									roundedCircle
								/>
								: <Image variant="top"
									className="myAccount_profileImage"
									src={imagePath}
									width={171}
									height={180}
									roundedCircle
								/>
							}
							<ImageUploader
								className='myAccount_imageUploader'
								buttonText='Edit Photo'
								onChange={uploadSingleFile}
								imgExtension={['.jpg', '.gif', '.png', '.gif']}
								maxFileSize={5242880}
								singleImage={true}
								withIcon={false}
								withPreview={false}
								label={false}
							/>

							<Card.Body>
								<Col md={{ offset: 0 }}>
									<Card.Text>
										<ListGroup variant="flush">
											<ListGroup.Item>User ID: <strong>{user.userID}</strong></ListGroup.Item>
											<ListGroup.Item>
												<Form.Group controlId="myAccount_firstName">
													<Form.Label>First name:</Form.Label>
													<Form.Control
														required
														type="text"
														placeholder="First name"
														value={firstName}
														onChange={onChangeFirstName}
														maxLength="100"
													/>
													<Form.Control.Feedback type="invalid">
														{v_fNMessage}
													</Form.Control.Feedback>
												</Form.Group>
											</ListGroup.Item>
											<ListGroup.Item>
												<Form.Group controlId="myAccount_lastName">
													<Form.Label>Last name:</Form.Label>
													<Form.Control
														required
														type="text"
														value={lastName}
														placeholder="Last name"
														onChange={onChangeLastName}
														maxLength="100"
													/>
													<Form.Control.Feedback type="invalid">
														{v_lNMessage}
													</Form.Control.Feedback>
												</Form.Group>
											</ListGroup.Item>
											<ListGroup.Item>
												<Form.Group controlId="email">
													<Form.Label>Email address:</Form.Label>
													<Form.Control
														type="email"
														placeholder="Email address"
														value={email}
														onChange={onChangeEmail}
														isInvalid={emailInvalid}
														maxLength="200"
														required />
													<Form.Control.Feedback type="invalid">
														{emailFeedBack}
													</Form.Control.Feedback>
												</Form.Group>
											</ListGroup.Item>
										</ListGroup>
									</Card.Text>
								</Col>
							</Card.Body>
							<Card.Footer>
								<Col md={{ span: 10, offset: 8 }}>
									<Button variant="light" type="button" onClick={cancelEdit}>Cancel</Button>
									<Button variant="light" type="submit">Update</Button>
								</Col>
							</Card.Footer>
						</Form>
					</Card>
				</div>
				: <div className="myAccount_userInformation">
					<Card >
						<h2>Hello {user.firstName} {user.lastName}</h2>
						<Image variant="top"
							className="myAccount_profileImage"
							src={imagePath}
							width={171}
							height={180}
							roundedCircle
						/>
						<Card.Body className="myAccount_userInformation_cardText">
							<Col md={{ span: 10, offset: 0 }}>
								<Card.Text >
									<ListGroup variant="flush" className="myAccount_userInformation_listGroup">
										<ListGroup.Item>User ID: <strong>{user.userID}</strong></ListGroup.Item>
										<ListGroup.Item>First Name: <strong>{firstName}</strong></ListGroup.Item>
										<ListGroup.Item>Last Name: <strong>{lastName}</strong></ListGroup.Item>
										<ListGroup.Item>E-Mail: <strong>{email}</strong></ListGroup.Item>
									</ListGroup>
								</Card.Text>
							</Col>
						</Card.Body>

						<Card.Footer>
							<Col md={{ span: 3, offset: 10 }}>
								<Button variant="light" onClick={editUser}>Edit</Button>
							</Col>
						</Card.Footer>
					</Card>
				</div>
			}

			{showResetPassword ?
				<CardDeck>
					<div className="myAccount_userMenu">
						<Row>
							<Card
								className='myAccount_resetPassword'
								onClick={() => setShowResetPassword(false)}
							>
								<Card.Img variant="top"
									src="/uploadImages/icon/reset_password.png"
									width={171}
									height={180} />
								<Card.Body>
									<Card.Title>Reset Password</Card.Title>
								</Card.Body>
							</Card>
							<Card
								className='myAccount_resetPassword'
								onClick={() => history.push('/forgot')}
							>
								<Card.Img variant="top"
									src="/uploadImages/icon/reset_password.png"
									width={171}
									height={180} />
								<Card.Body>
									<Card.Title>Reset Password</Card.Title>
								</Card.Body>
							</Card>
						</Row>
						<Row>
							<Card>
								<Card.Img variant="top" src="holder.js/100px160" />
								<Card.Body>
									<Card.Title>Card title</Card.Title>
									<Card.Text>
										This card has supporting text below as a natural lead-in to additional
        content.{' '}
									</Card.Text>
								</Card.Body>
								<Card.Footer>
									<small className="text-muted">Last updated 3 mins ago</small>
								</Card.Footer>
							</Card>
							<Card>
								<Card.Img variant="top" src="holder.js/100px160" />
								<Card.Body>
									<Card.Title>Card title</Card.Title>
									<Card.Text>
										This is a wider card with supporting text below as a natural lead-in to
										additional content. This card has even longer content than the first to
										show that equal height action.
      </Card.Text>
								</Card.Body>
								<Card.Footer>
									<small className="text-muted">Last updated 3 mins ago</small>
								</Card.Footer>
							</Card>
						</Row>
					</div>
				</CardDeck>

				:
				<Form className='myAccount_resetPassword_form'
					noValidate validated={resetPasswordValidated}
					onSubmit={resetPasswordSubmit}>
					<h1>Reset Password</h1>
					<Form.Group>
						<Form.Label >
							Current Password
    					</Form.Label>
						<Form.Control type="password" placeholder="Current Password"
							name="myAccount_resetPassword_currentPassword" />
					</Form.Group>
					<Form.Group className="myAccount_resetPassword_form_password">
						<Form.Label className="myAccount_resetPassword_form_password_label">New Password</Form.Label>
						<Form.Control
							type="password"
							placeholder="New Password"
							onChange={onChangePassword}
							isInvalid={passwordInvalid}
							minLength="8"
							maxLength="20"
							required />
						<Form.Control.Feedback type="invalid">
							{v_passwordMessage}
						</Form.Control.Feedback>
					</Form.Group>
					<Form.Group className="myAccount_resetPassword_form_repeatedPassword">
						<Form.Label className="myAccount_resetPassword_form_repeatedPassword_label">New Password Repeated</Form.Label>
						<Form.Control
							type="password"
							placeholder="New Password Repeated"
							required
							isInvalid={confirmPassword_invalid}
							onChange={onChangeConfirmPassword}
							minLength="8" maxLength="20"
						/>
						<Form.Control.Feedback type="invalid">
							{v_confirmPasswordMessage}
						</Form.Control.Feedback>
					</Form.Group>
					<Col md={{ span: 10, offset: 8 }}>
						<Button variant="light" type='submit'>Change Password</Button>
					</Col>
				</Form>
			}
		</CardDeck >


	);

}

export default MyAccount;
