import React, {useState, useEffect} from 'react';
import ReturnCarousel from './ReturnCarousel'
import {Card, CardDeck, Carousel} from 'react-bootstrap';

function ReadBlogList(props) {

	let title = props.title;
	let desc = props.desc;
	let writer = props.writer;
	let imagePathArray = props.imagePathArray;

	let [carousels, setCarousels] = useState([imagePathArray[0]]);

	 useEffect(() => {
		
		// for(let i =1; i<imagePathArray.length; i++){
		 setCarousels(<ReturnCarousel imagePath = {imagePathArray[0]}></ReturnCarousel>);
	// }
		// return carousels;
	})

	return(
		<div>
			<CardDeck>
			<Card>
				<Carousel>
					{/* {carousels} */}
					<Carousel.Item>
				<img
				className=""
				src={require('./1.jpg')}
				alt='BlogImages'
				/>
			</Carousel.Item>
				</Carousel>
				<Card.Body>
				<Card.Title>{title}</Card.Title>
				<Card.Text>
						{desc}
				</Card.Text>
				</Card.Body>
				<Card.Footer>
					<small className="text-muted">writer:{writer}</small>
				</Card.Footer>
			</Card>
			</CardDeck>
			</div>
	);
}

export default ReadBlogList;
