import {
	REGISTER_SUCCESS,
	REGISTER_FAIL,
	LOGIN_SUCCESS,
	LOGIN_FAIL,
	LOGOUT,
	UPDATE_SUCCESS,
	UPDATE_FAIL
} from "../Actions/types";

const user = JSON.parse(localStorage.getItem("user"));

const initialState = user
	? { isLoggedIn: true, user }
	: { isLoggedIn: false, user: null };

export default function (state = initialState, action) {
	const { type, payload } = action;
	switch (type) {
		case REGISTER_SUCCESS:
			return {
				...state,
				isLoggedIn: false,
			};
		case REGISTER_FAIL:
			return {
				...state,
				isLoggedIn: false,
			};
		case LOGIN_SUCCESS:
			return {
				...state,
				isLoggedIn: true,
				user: payload.user,
			};
		case LOGIN_FAIL:
			return {
				...state,
				isLoggedIn: false,
				user: null,
			};
		case LOGOUT:
			return {
				...state,
				isLoggedIn: false,
				user: null,
			};
		case UPDATE_SUCCESS:
			return {
				...state,
				isLoggedIn: true,
				user: payload.user
			};
		case UPDATE_FAIL:
			return {
				...state,
				isLoggedIn: true,
				user: user
			};
		default:
			return state;
	}
}
