import React, {Component} from 'react';
import TOC from "./PostComponents/TOC"
import ReadContent from "./PostComponents/ReadContent"
import Subject from "./PostComponents/Subject"
import Control from "./PostComponents/Control"
import UpdateContent from "./PostComponents/UpdateContent"
import CreateContent from './PostComponents/CreateContent';
import axios from 'axios';
import { Button } from 'reactstrap';




class Board extends Component{
	constructor(props){
		super(props);
		
		this.state ={
		mode:'list',
      	selected_content_id:0,
      	// welcome:{title:'Welcome', desc:'Hello, React!!'},
      	subject:{title:'Welcome to My project1', sub:"Developing Board..."},
		contents:[]
	}
	this.max_content_id=this.max_content_id;

}	
	// receive data by using fetch
	componentDidMount() {
		console.log('componentDidMount()');
		
		fetch('/board/senddata',
		{
			method: 'POST',
			headers:{
				'Accept': 'application/json'
			}
			})
		.then (res => res.json())
		.then (data1 => this.setState({contents: data1.rows}))
	}

	// receive data by using axios (don't need to convert)
	setData(_writer,_title,_desc,_mode) {
		console.log('setData()');
		let data = {
			writer:_writer,
			title:_title,
			description:_desc,
			mode:_mode
		}

		axios.post('/board/receivedata',data)
		.then(function(response) { 
			this.setState({contents: response.data.rows[1]}) })
		.catch(function (error) {
		console.log(error);
		});
		
		
	// 	fetch('http://localhost:4000/board/giveresult'
	// 	,{
	// 	method: 'POST',
	// 	headers:{
	// 		'Content-Type': 'application/json',
    // 		'Accept': 'application/json'
	// 	},
	// 	body:JSON.stringify(data)
		
	// 	})
	// 	.then (console.log(data))
	// 	.then (res => res.json())
	// 	.then (data => console.log(data));
  	}
	

	getReadContent(){
		console.log('getReadContent()');
		let i =0;
		  while(i<this.state.contents.length){
			let data = this.state.contents[i];
			if(data.id === this.state.selected_content_id){
			  return data;
			  break;
			}
			i=i+1;
		  }
	  }
	  
	getContent(){
		console.log('getContent()');
		let _title, _writer, _desc,_article,_content = null;
		if(this.state.mode ==='list'){
			// console.log(this.state.contents)
			// this. max_content_id = this.state.contents[this.state.contents.length].id
			// console.log(this.max_content_id)
		}

		//read
		else if (this.state.mode ==='read'){
		  _content = this.getReadContent();
			console.log(_content);
		  _article = <ReadContent writer={_content.writer} title= {_content.title} desc= {_content.description} ></ReadContent>
		}

		// Update
		else if(this.state.mode ==='update'){
		  _content = this.getReadContent();
		  _article = <UpdateContent data = {_content} onSubmit={
			function(_id, _writer, _title, _desc){
			  //add content to this.state.cotnents
			  let _contents = Array.from(this.state.contents);
			  let i =0;
			  while(i<_contents.length){
				if(_contents[i].id ===_id){
				  _contents[i] = {id:_id, writer:_writer, title:_title, description:_desc};
				  break;
				}
				i=i+1;
			  }
			  this.setState({
				contents:_contents,
				mode:'read'
			  });
			this.setData();
			}.bind(this)
		  }></UpdateContent>
		 
		}

		//create

		else if (this.state.mode ==='create'){
			_article = <CreateContent onSubmit={
				 async function(_writer,_title,_desc){
				//add content to this.state.cotnents
				// this.max_content_id= this.state.contents[this.state.contents.length-1].id+1;
				// var _contents = Array.from(this.state.contents);
				// _contents.push({id:this.max_content_id, writer: _writer, title:_title, description:_desc});
				await this.setData(_writer,_title,_desc,'create');
				console.log(this.state.contents)
				await this.setState({
					// contents:_contents,
					selected_content_id:this.state.contents[this.state.contents.length-1].id,
					mode:'read'
				});
				}.bind(this)
			}></CreateContent>
		}
		return _article;
	  }

	//   creatArticle(){
	// 	var _create = null;
	// 		// Create
	// 		_create = <CreateContent onSubmit={
	// 			function(_writer,_title,_desc){
	// 			//add content to this.state.cotnents
	// 			// this.max_content_id= this.state.contents[this.state.contents.length-1].id+1;
	// 			// var _contents = Array.from(this.state.contents);
	// 			// _contents.push({id:this.max_content_id, writer: _writer, title:_title, description:_desc});
	// 			this.setData(_writer,_title,_desc,'create');
	// 			this.setState({
	// 				// contents:_contents,
	// 				selected_content_id:this.state.contents[this.state.contents.length-1].id,
	// 				mode:'read'
	// 			});
	// 			this.getContent();
	// 			}.bind(this)
				
	// 		}></CreateContent>
	// 		if(this.state.mode!='update'){
	// 		return _create;
	// 		}
	// 		else return null;	
	//   }

	  createController() {
		
		  let _button;
		if(this.state.mode ==='read' || this.state.mode === 'update'){
			console.log('createController()');
			_button = <Control onChangeMode={function(_mode){
				if(_mode === 'delete'){
					if(window.confirm('really?')){
						let _content = Array.from(this.state.contents);
						let i = 0;
						while(i<this.state.contents.length){
						if(_content[i].id === this.state.selected_content_id){
							_content.splice(i,1);
							break;
						}
						i =i+1
						}
						this.setState({
						mode:'list',
						contents:_content,
						});
						alert('deleted!');
					}
				}
				else{
				this.setState({
				mode:_mode
				});			
			}
			}.bind(this)}></Control>
			return _button;
		} 	
		else {return null;}
	  }

	  createPost() {
		
		  let _post;
		if (this.state.mode !=='create') {
			console.log('createPost()');
			_post = <Button onClick = {function(e){
				e.preventDefault();
				this.setState({mode: 'create'})
			}.bind(this)}type="button">Add New Post</Button>

			return _post;
		}
		else return null;
	  }

	  createList(){
		  
		  if(this.state.mode === 'list'){
			console.log('createList()');
			let _list = <TOC onChangePage = {
				function(id){ 
				this.setState({
					mode:'read',
					selected_content_id:Number(id)//문자를 숫자로 변경해주는 함수 Number
				});
				}.bind(this)
			} data={this.state.contents}></TOC>
			return _list;
		}
		return null;
	  }

	  render(){
    
		return(
		<div className="Board">
			<Subject title={this.state.subject.title} 
			sub={this.state.subject.sub}
			onChangePage={function(){
			this.setState({mode:'list'});
			
			}.bind(this)}
			></Subject>

			{this.createList()}

			{this.getContent()}
			
			{this.createController()}

			{this.createPost()}			
		
			{/* {this.creatArticle()} */}
		</div>
		);
	  }
	}


export default Board;
