import React, {useState, useEffect} from 'react';
import {Button, Image, Card, CardDeck, ListGroup, CardColumns} from 'react-bootstrap';
import '../css/Blog.css'
import axios from 'axios';

function BlogList(props){
	let [data, setData] =  useState(null);

	let donwloadedData='';
	let imagePaths;
	let writer;
	let title;
	let desc;
	let splittedImagePaths

	let [showAllBlogs, setShowAllBlogs] = useState(null);

	useEffect(()=>{
		const res =  axios.post("/blog/list")
		.then (response => {
			donwloadedData = response.data;
			console.log(donwloadedData);
			
			// for(let i = 0; i<donwloadedData.length; i++){
			
			writer = donwloadedData[0].writer;
			title = donwloadedData[0].title;
			desc = donwloadedData[0].desc;
			imagePaths = donwloadedData[0].imagePaths;

			if(imagePaths != null){
				  splittedImagePaths = imagePaths.split(',');
				}

			showAllBlogs = function showBlogs() {
				let blogs =
				<div>
				<CardDeck>
				<Card>
					<Card.Img variant="top" src={splittedImagePaths} />
					<Card.Body>
					<Card.Title>{title}</Card.Title>
					<Card.Text>
							{desc}
					</Card.Text>
					</Card.Body>
					<Card.Footer>
			          <small className="text-muted">writer:{writer}</small>
					</Card.Footer>
				</Card>
				</CardDeck>
				</div>
				return(blogs)
				}
			// }
		})
	}, []);

	return(
		<div className="blog">
			{showAllBlogs}
			
			<Button variant="primary" href='/bloglist/create'>Add Blog</Button>
		</div>

	)

}

export default BlogList;
