import React, { Component } from 'react';
import { Button, FormGroup, Label, Input } from 'react-bootstrap';
class UpdateContent extends Component {
	constructor(props) {
		super(props);
		this.state = {
			id: this.props.data.id,
			writer: this.props.data.writer,
			title: this.props.data.title,
			desc: this.props.data.description
		}
		this.inputFormHandler = this.inputFormHandler.bind(this);
	}

	inputFormHandler(e) {

		this.setState({ [e.target.name]: e.target.value });
	}

	render() {
		console.log('Update Content Render');
		console.log(this.props.data);
		return (

			<article>
				<h2>Edit Post</h2>
				<form action="/create_process" method="post" onSubmit={function (e) {
					e.preventDefault();
					this.props.onSubmit(
						this.state.id,
						this.state.writer,
						this.state.title,
						this.state.desc
					);
				}.bind(this)}>
					<FormGroup>
						<Label for="title">Writer</Label>
						<Input type="text" name="writer" placeholder="WRITER" value={this.state.writer}
							onChange={this.inputFormHandler} />
					</FormGroup>
					<FormGroup>
						<Label for="writer">Title</Label>
						<Input type="text" name="title" placeholder="TITLE" value={this.state.title}
							onChange={this.inputFormHandler} />

					</FormGroup>
					<FormGroup>
						<Label for="desc">Text Area</Label>
						<Input type="textarea" name="desc" placeholder="TEXT" onChange={this.inputFormHandler}
							value={this.state.desc} />
					</FormGroup>

					<input type="hidden" name="id" value={this.state.id}></input>

					<p>
						<input
							type="text"
							name="writer"
							placeholder="writer"
							value={this.state.writer}
							onChange={this.inputFormHandler}
						></input>
					</p>

					<p>
						<input
							type="text"
							name="title"
							placeholder="title"
							value={this.state.title}
							onChange={this.inputFormHandler}
						></input>
					</p>
					<p>
						<textarea
							name="desc"
							placeholder="description"
							onChange={this.inputFormHandler}
							value={this.state.desc}></textarea>
					</p>
					<p>
						<input type="submit"></input>
					</p>
				</form>
			</article>
		);
	}
}

export default UpdateContent;
