import React, { useState } from 'react';
import { useDispatch, useSelector } from "react-redux";
import { CardDeck, Card, Image, ListGroup, Button, Col } from 'react-bootstrap';
import { Redirect } from 'react-router-dom';
import '../css/MyAccount.css';

function MyAccount(props) {
	const { isLoggedIn, user } = useSelector(state => state.auth);
	const IMG_WIDTH = 171;
	const IMG_LENGTH = 180;
	const mailImagePath = "./uploadImages/icon/mail.jpg";
	let imagePath;

	if (!isLoggedIn) {
		return <Redirect to="/home" />;
	}
	else {
		imagePath = user.profileImg;
		if (imagePath != null) {
			imagePath = imagePath.substring(16, imagePath.length);
		}
		else imagePath = "./uploadImages/icon/img_user.jpg"
	}
	const editUser = () => {

	}
	return (
		<CardDeck className="myAccount">

			<Card>

				<Col md={{ span: 10, offset: 4 }}>
					<Col md={{ offset: 2 }}>
						<h2>{user.firstName} {user.lastName}</h2>
					</Col>
					<Image variant="top"
						src={imagePath}
						width={171}
						height={180}
						rounded
					/>
				</Col>
				<Card.Body>
					<Col md={{ span: 10, offset: 1 }}>
						<Card.Text>
							<ListGroup variant="flush">
								<ListGroup.Item>User ID: <strong>{user.userID}</strong></ListGroup.Item>
								<ListGroup.Item>First Name: <strong>{user.firstName}</strong></ListGroup.Item>
								<ListGroup.Item>Last Name: <strong>{user.lastName}</strong></ListGroup.Item>
								<ListGroup.Item>E-Mail: <strong>{user.email}</strong></ListGroup.Item>
							</ListGroup>
						</Card.Text>
					</Col>
				</Card.Body>
				<Card.Footer>
					<Col md={{ span: 3, offset: 10 }}>
						<Button variant="light" onChange={editUser}>Edit</Button>
					</Col>
				</Card.Footer>
			</Card>
			<Card>
				<Card.Img variant="top" src="holder.js/100px160" />
				<Card.Body>
					<Card.Title>Card title</Card.Title>
					<Card.Text>
						This card has supporting text below as a natural lead-in to additional
        content.{' '}
					</Card.Text>
				</Card.Body>
				<Card.Footer>
					<small className="text-muted">Last updated 3 mins ago</small>
				</Card.Footer>
			</Card>
			<Card>
				<Card.Img variant="top" src="holder.js/100px160" />
				<Card.Body>
					<Card.Title>Card title</Card.Title>
					<Card.Text>
						This is a wider card with supporting text below as a natural lead-in to
						additional content. This card has even longer content than the first to
						show that equal height action.
      </Card.Text>
				</Card.Body>
				<Card.Footer>
					<small className="text-muted">Last updated 3 mins ago</small>
				</Card.Footer>
			</Card>
		</CardDeck >
	);

}

export default MyAccount;
