import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from "react-redux";
import { Table } from 'react-bootstrap';
import { useHistory } from "react-router-dom";
import AdminPageService from '../Services/AdminPage.service'
import {
	unauthorized_access
} from "./message";
import {
	GET_USERS_SUCCESS,
	USER_NOT_FOUND
} from "./type";

function AdminPage(props) {

	const { isLoggedIn, user } = useSelector(state => state.auth);
	const [users, setUsers] = useState(null);
	let history = useHistory();

	useEffect(() => {
		if (!isLoggedIn || user.role != 1) {
			alert(unauthorized_access);
			history.push('/home');
		}
		AdminPageService.get_all_users(user)
			.then(response => {
				console.log(response.data);
				if (response.data.message === USER_NOT_FOUND) {
					setUsers(null);
				}
				else if (response.data.message === GET_USERS_SUCCESS) {
					setUsers(response.data.users);
				}
			})
	}, [])

	return (

		<Table striped bordered hover variant="dark">
			<thead>
				<tr>
					<th>#</th>
					<th>First Name</th>
					<th>Last Name</th>
					<th>Username</th>
				</tr>
			</thead>
			<tbody>
				<tr>
					<td>1</td>
					<td>Mark</td>
					<td>Otto</td>
					<td>@mdo</td>
				</tr>
				<tr>
					<td>2</td>
					<td>Jacob</td>
					<td>Thornton</td>
					<td>@fat</td>
				</tr>
				<tr>
					<td>3</td>
					<td colSpan="2">Larry the Bird</td>
					<td>@twitter</td>
				</tr>
			</tbody>
		</Table>
	);
}

export default AdminPage;
