import React, {useState, useEffect} from 'react';
import {Card, CardDeck, Carousel} from 'react-bootstrap';

function ReadBlogList(props) {

	let title = props.title;
	let desc = props.desc;
	let writer = props.writer;
	let imagePathArray = props.imagePathArray;
	let ReadBlogList_keyValue =-1;
	let carousel_control = true;
	//Decision of showing arrow in the picures
	if(imagePathArray.length > 1) carousel_control = false;
	else carousel_control = true;
	return(
		<div>
			
			<Card style={{ width: '20rem' }}>

			<Carousel interval={null} controls={false}>
				{
					imagePathArray.map((data)=>{
						console.log(imagePathArray.length);
						ReadBlogList_keyValue = ReadBlogList_keyValue+1;
						return(
							<Carousel.Item key = {ReadBlogList_keyValue}>
								<img								
								className="blog_images"
								src={data}
								alt='BlogImages'
								/>
							</Carousel.Item>
							)						
					})
				}
			</Carousel>	
				<Card.Body>
				<Card.Title>{title}</Card.Title>
				<Card.Text>
						{desc}
				</Card.Text>
				</Card.Body>
				<Card.Footer>
					<small className="text-muted">writer:{writer}</small>
				</Card.Footer>
			</Card>
			
			</div>
	);
}

export default ReadBlogList;
