import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
import { useHistory } from "react-router-dom";
import { ListGroup, OverlayTrigger } from 'react-bootstrap';

function BlogComments(props) {

	let [comments, setComments] = useState(props.comments.slice(1, props.comments.length))
	let key_blogComments = -1;

	const delete_comment = () => {

	}

	const popover = (
		<Popover id="popover-basic">
			<Popover.Title as="h3">Popover right</Popover.Title>
			<Popover.Content>
				And here's some <strong>amazing</strong> content. It's very engaging.
      right?
    </Popover.Content>
		</Popover>
	);
	return (
		<>
			<ListGroup variant="flush">

				{
					comments.map((data) => {
						key_blogComments = key_blogComments + 1;
						return (
							<ListGroup.Item key={key_blogComments}>{data}
								{
									<OverlayTrigger trigger="click" placement="right" overlay={popover}>
										<Button variant="light" size="sm" onClick={delete_comment}>Delete</Button>
									</OverlayTrigger>
								}
							</ListGroup.Item>
						)
					})
				}
			</ListGroup>
		</>
	);
}



export default BlogComments;
